export default class StaticProvider {
  constructor(config) {
    this.config = config
    this.static = true
  }

  getLatestVersion() {
    return Promise.resolve({
      version: 'static',
      link: '',
      filename: '',
      description: '',
      log: '',
    })
  }

  downloadVersion(){
    return Promise.reject(new Error('Static provider does not support downloading'))
  }
  
  checkConfig() {
    return Promise.resolve()
  }
}
